import React, { useState, useEffect } from 'react';
import { jwtDecode } from 'jwt-decode';

function AddEvent() {
    // Define state for each event field
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [startTime, setStartTime] = useState('');
    const [endTime, setEndTime] = useState('');
    const [location, setLocation] = useState('');
    const [attendees, setAttendees] = useState([]);
    const [users, setUsers] = useState([]);
    const [responseMessage, setResponseMessage] = useState('');
    const [loggedInUserId, setLoggedInUserId] = useState('');

    // Fetch the logged-in user's ID and users when the component loads
    useEffect(() => {
        const token = localStorage.getItem('token');
        if (token) {
            try {
                const decoded = jwtDecode(token);
                console.log('Decoded token:', decoded);
                // Assuming the token contains _id for the logged-in user
                setLoggedInUserId(decoded._id);
            } catch (error) {
                console.error('Error decoding token:', error);
            }
        }
    }, []);

    // Fetch users when the logged-in user ID is available
    useEffect(() => {
        if (!loggedInUserId) return;  // Wait until the logged-in user ID is set

        const fetchUsers = async () => {
            try {
                const response = await fetch('http://localhost:5000/users'); // Adjust URL as needed
                const data = await response.json();

                // Filter out the logged-in user
                const filteredUsers = data.filter(user => user._id !== loggedInUserId);
                setUsers(filteredUsers); // Set users state with the filtered user list
            } catch (error) {
                console.error('Error fetching users:', error);
            }
        };

        fetchUsers();
    }, [loggedInUserId]);  // Fetch users only when loggedInUserId is available

    const handleSubmit = async (e) => {
        e.preventDefault();  // Prevent page refresh
        const token = localStorage.getItem('token');

        // Make a POST request with event details
        try {
            const response = await fetch('http://localhost:5000/events', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`,
                },
                body: JSON.stringify({
                    title,
                    description,
                    startTime,
                    endTime,
                    location,
                    userId: loggedInUserId, // Use the logged-in user's ID
                    attendees,
                }),
            });

            const data = await response.json();
            setResponseMessage(data.message || 'Event added successfully');
        } catch (error) {
            console.error('Error adding event:', error);
            setResponseMessage('Error adding event');
        }
    };

    // Update selected attendees when checkbox is toggled
    const handleAttendeeChange = (e) => {
        const userId = e.target.value;
        setAttendees((prevAttendees) => {
            const updatedAttendees = e.target.checked
                ? [...prevAttendees, userId] // Add user ID if checked
                : prevAttendees.filter((id) => id !== userId); // Remove user ID if unchecked
            console.log("Updated attendees:", updatedAttendees); // Log updated attendees to check
            return updatedAttendees;
        });
    };

    return (
        <div style={{ padding: '20px' }}>
            <form onSubmit={handleSubmit}>
                <div>
                    <label htmlFor="title">Title: </label>
                    <input
                        type="text"
                        id="title"
                        value={title}
                        onChange={(e) => setTitle(e.target.value)}
                    />
                </div>

                <div>
                    <label htmlFor="description">Description: </label>
                    <textarea
                        id="description"
                        value={description}
                        onChange={(e) => setDescription(e.target.value)}
                    />
                </div>

                <div>
                    <label htmlFor="startTime">Start Time: </label>
                    <input
                        type="datetime-local"
                        id="startTime"
                        value={startTime}
                        onChange={(e) => setStartTime(e.target.value)}
                    />
                </div>

                <div>
                    <label htmlFor="endTime">End Time: </label>
                    <input
                        type="datetime-local"
                        id="endTime"
                        value={endTime}
                        onChange={(e) => setEndTime(e.target.value)}
                    />
                </div>

                <div>
                    <label htmlFor="location">Location: </label>
                    <input
                        type="text"
                        id="location"
                        value={location}
                        onChange={(e) => setLocation(e.target.value)}
                    />
                </div>

                <div>
                    <label htmlFor="attendees">Attendees: </label>
                    <div>
                        {users.length === 0 ? (
                            <p>Loading users...</p>
                        ) : (
                            users.map((user) => (
                                <div key={user._id}>
                                    <label>
                                        <input
                                            type="checkbox"
                                            value={user._id}
                                            onChange={handleAttendeeChange}
                                            checked={attendees.includes(user._id)} // Check if the user ID is in the attendees list
                                        />
                                        {user.username}
                                    </label>
                                </div>
                            ))
                        )}
                    </div>
                </div>

                <button type="submit">Add Event</button>
            </form>

            {responseMessage && <p>{responseMessage}</p>}
        </div>
    );
}

export default AddEvent;
