import React, {useState} from 'react'
import Visuals from './Visuals';

function App()
{
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [responseMessage, setResponseMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('http://localhost:5000/submit', { //holds response object
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({username, password}),
      });

      const data = await response.json(); //holds the parsed JSON data from the response body.
      setResponseMessage(data.message);
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return(
    <div>
    {/* Pass state and handleSubmit function as props to Visuals */}
    <Visuals 
      username={username} 
      setUsername={setUsername}
      password={password} 
      setPassword={setPassword}
      handleSubmit={handleSubmit} 
      responseMessage={responseMessage} 
    />
  </div>
  );
}



export default App;
