import React, { useState } from 'react';

function AddEvent() {
    // Define state for each event field
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [startTime, setStartTime] = useState('');
    const [endTime, setEndTime] = useState('');
    const [location, setLocation] = useState('');
    const [attendees, setAttendees] = useState([]);
    const [users, setUsers] = useState([]);
    const [responseMessage, setResponseMessage] = useState('');

    // Fetch available users when component loads
    useEffect(() => {
        const fetchUsers = async () => {
            try {
                const response = await fetch('http://localhost:5000/users'); // Adjust URL as needed
                const data = await response.json();
                setUsers(data); // Set users state with fetched user data
            } catch (error) {
                console.error('Error fetching users:', error);
            }
        };

        fetchUsers();
    }, []);

    // Handle form submission
    const handleSubmit = async (e) => {
        e.preventDefault();  // Prevent page refresh

        // Parse attendees as an array of user IDs
        const attendeesArray = attendees.split(',').map(id => id.trim());

        // Make a POST request with event details
        const response = await fetch('http://localhost:5000/events', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                title,
                description,
                startTime,
                endTime,
                location,
                userId: "YOUR_USER_ID", // Replace with the logged-in user ID
                attendees: attendeesArray,
            }),
        });

        const data = await response.json();
        setResponseMessage(data.message || 'Event added successfully');
    };

    return (
        <div style={{ padding: '20px' }}>
            <form onSubmit={handleSubmit}>
                <div>
                    <label htmlFor="title">Title: </label>
                    <input
                        type="text"
                        id="title"
                        value={title}
                        onChange={(e) => setTitle(e.target.value)}
                    />
                </div>

                <div>
                    <label htmlFor="description">Description: </label>
                    <textarea
                        id="description"
                        value={description}
                        onChange={(e) => setDescription(e.target.value)}
                    />
                </div>

                <div>
                    <label htmlFor="startTime">Start Time: </label>
                    <input
                        type="datetime-local"
                        id="startTime"
                        value={startTime}
                        onChange={(e) => setStartTime(e.target.value)}
                    />
                </div>

                <div>
                    <label htmlFor="endTime">End Time: </label>
                    <input
                        type="datetime-local"
                        id="endTime"
                        value={endTime}
                        onChange={(e) => setEndTime(e.target.value)}
                    />
                </div>

                <div>
                    <label htmlFor="location">Location: </label>
                    <input
                        type="text"
                        id="location"
                        value={location}
                        onChange={(e) => setLocation(e.target.value)}
                    />
                </div>

                <div>
                    <label htmlFor="attendees">Attendees (comma-separated user IDs): </label>
                    <input
                        type="text"
                        id="attendees"
                        value={attendees}
                        onChange={(e) => setAttendees(e.target.value)}
                    />
                </div>

                <button type="submit">Add Event</button>
            </form>

            {responseMessage && <p>{responseMessage}</p>}
        </div>
    );
}

export default AddEvent;
