import React, { useState, useEffect } from 'react';
const User = require('./loginDB/UserModel.js');

function AddEvent() {
    // Define state for each event field
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [startTime, setStartTime] = useState('');
    const [endTime, setEndTime] = useState('');
    const [location, setLocation] = useState('');
    const [attendees, setAttendees] = useState([]);
    const [users, setUsers] = useState([]);
    const [responseMessage, setResponseMessage] = useState('');

    // Fetch available users when component loads
    useEffect(() => {
        const fetchUsers = async () => {
            try {
                const response = await fetch('http://localhost:5000/users'); // Adjust URL as needed
                const data = await response.json();
                setUsers(data); // Set users state with fetched user data
            } catch (error) {
                console.error('Error fetching users:', error);
            }
        };

        fetchUsers();
    }, []);

    // Handle form submission
    const loggedInUserId = User.findOne({ username: username });

    const handleSubmit = async (e) => {
        e.preventDefault();  // Prevent page refresh

        // Make a POST request with event details
        try {
            const response = await fetch('http://localhost:5000/events', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    title,
                    description,
                    startTime,
                    endTime,
                    location,
                    userId: "nayeon", // Replace with the logged-in user ID
                    attendees,
                }),
            });

            const data = await response.json();
            setResponseMessage(data.message || 'Event added successfully');
        } catch (error) {
            console.error('Error adding event:', error);
            setResponseMessage('Error adding event');
        }
    };

    // Update selected attendees when dropdown value changes
    const handleAttendeeChange = (e) => {
        const userId = e.target.value;
        setAttendees((prevAttendees) =>
            e.target.checked
                ? [...prevAttendees, userId] // Add user ID if checked
                : prevAttendees.filter((id) => id !== userId) // Remove user ID if unchecked
        );
    };

    return (
        <div style={{ padding: '20px' }}>
            <form onSubmit={handleSubmit}>
                <div>
                    <label htmlFor="title">Title: </label>
                    <input
                        type="text"
                        id="title"
                        value={title}
                        onChange={(e) => setTitle(e.target.value)}
                    />
                </div>

                <div>
                    <label htmlFor="description">Description: </label>
                    <textarea
                        id="description"
                        value={description}
                        onChange={(e) => setDescription(e.target.value)}
                    />
                </div>

                <div>
                    <label htmlFor="startTime">Start Time: </label>
                    <input
                        type="datetime-local"
                        id="startTime"
                        value={startTime}
                        onChange={(e) => setStartTime(e.target.value)}
                    />
                </div>

                <div>
                    <label htmlFor="endTime">End Time: </label>
                    <input
                        type="datetime-local"
                        id="endTime"
                        value={endTime}
                        onChange={(e) => setEndTime(e.target.value)}
                    />
                </div>

                <div>
                    <label htmlFor="location">Location: </label>
                    <input
                        type="text"
                        id="location"
                        value={location}
                        onChange={(e) => setLocation(e.target.value)}
                    />
                </div>

                <div>
                    <label htmlFor="attendees">Attendees: </label>
                    <div>
                        {users.map((user) => (
                            <div key={user._id}>
                                <label>
                                    <input
                                        type="checkbox"
                                        value={user._id}
                                        onChange={handleAttendeeChange}
                                        checked={attendees.includes(user._id)}
                                    />
                                    {user.username}
                                </label>
                            </div>
                        ))}
                    </div>
                </div>

                <button type="submit">Add Event</button>

            </form>

            {responseMessage && <p>{responseMessage}</p>}
        </div>
    );
}

export default AddEvent;
