import React, {useState} from 'react'

function loginInput({username, password, setResponseMessage, setUsername, setPassword})
{
    const handleSubmit = async (e) => {
        e.preventDefault();
    
        try {
          const response = await fetch('http://localhost:5000/submit', { //holds response object
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify({username, password}),
          });
    
          const data = await response.json(); //holds the parsed JSON data from the response body.
          setResponseMessage(data.message);
        } catch (error) {
          console.error('Error:', error);
        }


    }

};

export default loginInput
